<template>
  <div class="row items-center">
    <a
      v-if="websiteLink !== undefined"
      :href="websiteLink.url"
      class="text-black"
      target="_blank"
    >
      <q-icon name="link" size="md">
        <q-tooltip>Visit Website</q-tooltip>
      </q-icon>
    </a>
    <a v-if="emailLink !== undefined" :href="emailLink.url" class="text-black"
      ><q-icon name="mail" size="md">
        <q-tooltip>Send Email</q-tooltip>
      </q-icon>
    </a>
    <a
      v-for="(link, index) in webLinks"
      :key="index"
      :href="link.url"
      :class="'icon icon-' + link.linkType"
      target="_blank"
    >
      <q-tooltip>{{ link.url }}</q-tooltip>
    </a>
  </div>
</template>

<script>
import libraries from "../../data/libraries.json";
import linktools from "../lib/links";

export default {
  name: "LinkPanel",

  props: {
    links: Array
  },

  data() {
    return {
      libraries
    };
  },

  computed: {
    websiteLink: function() {
      return linktools.getWebsiteLink(this.links);
    },
    emailLink: function() {
      return linktools.getEmailLink(this.links);
    },
    webLinks: function() {
      return linktools.getWebLinks(this.links);
    }
  },
  
  methods: {}
};
</script>

